mapper.select:
SELECT BANNER_INQUIRY_KEY, BANNER_VIEW_SEQ, BANNER_TITLE, BANNER_START_DATE, BANNER_END_DATE, IF((BANNER_LINK_TYPE) = '1','현재창','새창') BANNER_LINK_TYPE, BANNER_LINK_URL, IF((BANNER_STATUS) = '1','노출','비노출') BANNER_STATUS, (SELECT COUNT(*) FROM BANNER_MASTER WHERE banner_status = '1' ) AS BANNER_COUNT FROM BANNER_MASTER WHERE BANNER_INSERT_DATE IS NOT NULL
AND BANNER_STATUS = #{searchValue}
ORDER BY BANNER_VIEW_SEQ LIMIT #{rowStartNumber}, #{pageGroupSize}
