mapper.select:
SELECT BOARD_TRAND_KEY, FN_GET_COMMON_CODE_NAME('TRAND_CATEGORY', TRAND_CATEGORY) TRAND_CATEGORY, TRAND_TITLE, TRAND_CONTENTS, TRAND_ORIGIN, TRAND_ORIGIN_LINK, TRAND_INSERT_NAME, TRAND_INSERT_DATE FROM BOARD_TRAND WHERE TRAND_REMOVE_DATE IS NULL
AND ( TRAND_INSERT_DATE BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND TRAND_CATEGORY = #{searchValue}
AND TRAND_ORIGIN = #{searchValue1}
AND TRAND_TITLE LIKE CONCAT('%', #{searchValue2}, '%')
AND TRAND_CONTENTS LIKE CONCAT('%', #{searchValue2}, '%')
AND ( TRAND_TITLE LIKE CONCAT('%', #{searchValue2}, '%') OR TRAND_CONTENTS LIKE CONCAT('%', #{searchValue2}, '%') )
AND ( TRAND_TITLE LIKE CONCAT('%', #{searchValue2}, '%') OR TRAND_CONTENTS LIKE CONCAT('%', #{searchValue2}, '%') OR TRAND_ORIGIN LIKE CONCAT('%', #{searchValue2}, '%') OR TRAND_ORIGIN_LINK LIKE CONCAT('%', #{searchValue2}, '%') OR TRAND_INSERT_NAME LIKE CONCAT('%', #{searchValue2}, '%') )
ORDER BY TRAND_INSERT_DATE DESC LIMIT #{rowStartNumber}, #{pageListSize}

SELECT bt.BOARD_TRAND_KEY, FN_GET_COMMON_CODE_NAME('TRAND_CATEGORY', TRAND_CATEGORY) TRAND_CATEGORY, bt.TRAND_TITLE, bt.TRAND_CONTENTS, bt.TRAND_ORIGIN, bt.TRAND_ORIGIN_LINK, bt.TRAND_INSERT_NAME, bt.TRAND_INSERT_DATE, af.FILE_KEY, af.FILE_TARGET_KEY, af.FILE_TARGET, af.FILE_NAME, af.FILE_SAVED_NAME, af.FILE_PATH, af.FILE_SIZE, af.FILE_EXTENSION, af.FILE_WIDTH, af.FILE_HEIGHT FROM BOARD_TRAND bt LEFT OUTER JOIN ADMINISTRATION_FILE af ON bt.BOARD_TRAND_KEY = af.file_target_key AND af.file_target = 'boardTrand'
AND af.file_type = #{fileType}
WHERE bt.BOARD_TRAND_KEY = #{boardTrandKey} GROUP BY bt.BOARD_TRAND_KEY

SELECT bsb.BOARD_SOCIAL_BLOG_KEY, bsb.SOCIAL_LINK, bsb.SOCIAL_ORIGIN, bsb.SOCIAL_BLOG_TITLE, bsb.SOCIAL_BLOG_CONTENT, bsb.SOCIAL_BLOG_INSERT_NAME, bsb.SOCIAL_BLOG_INSERT_DATE, bsb.SOCIAL_BLOG_REMOVE_DATE, af.FILE_KEY, af.FILE_TARGET_KEY, af.FILE_TARGET, af.FILE_NAME, af.FILE_SAVED_NAME, af.FILE_PATH, af.FILE_SIZE, af.FILE_EXTENSION, af.FILE_WIDTH, af.FILE_HEIGHT FROM BOARD_SOCIAL_BLOG bsb LEFT OUTER JOIN ADMINISTRATION_FILE af ON bsb.BOARD_SOCIAL_BLOG_KEY = af.file_target_key AND af.file_target = 'boardSocialBlog'
AND af.file_type = #{fileType}
WHERE bsb.BOARD_SOCIAL_BLOG_KEY = #{boardSocialBlogKey} GROUP BY bsb.BOARD_SOCIAL_BLOG_KEY

mapper.update:
UPDATE BOARD_SOCIAL_BLOG SET 
  SOCIAL_BLOG_TITLE = #{socialBlogTitle},
  SOCIAL_BLOG_CONTENT = #{socialBlogContent}
WHERE
  BOARD_SOCIAL_BLOG_KEY = #{boardSocialBlogKey}

mapper.select:
SELECT bt.BOARD_TRAND_KEY, FN_GET_COMMON_CODE_NAME('TRAND_CATEGORY', bt.TRAND_CATEGORY) TRAND_CATEGORY_NAME, bt.TRAND_TITLE, bt.TRAND_CONTENTS, bt.TRAND_ORIGIN, bt.TRAND_ORIGIN_LINK, bt.TRAND_INSERT_NAME, bt.TRAND_INSERT_DATE, af.FILE_KEY, af.FILE_TARGET_KEY, af.FILE_TARGET, af.FILE_NAME, af.FILE_SAVED_NAME FROM BOARD_TRAND bt LEFT OUTER JOIN ADMINISTRATION_FILE af ON bt.BOARD_TRAND_KEY = af.file_target_key AND af.file_target = 'boardTrand' AND af.file_type = 2 where TRAND_REMOVE_DATE IS NULL
AND TRAND_CATEGORY = #{trandCategory}
order by TRAND_INSERT_DATE DESC LIMIT #{rowStartNumber}, #{pageListSize}
