mapper.insert:
INSERT INTO
	CONTRACT_USER (
		contract_master_key,
		user_master_key,
		contract_user_id
	) VALUES (
		#{contractMasterKey},
		#{userMasterKey},
		#{contractUserId}
	)

mapper.select:
SELECT pm.payment_master_key, pm.payment_oid, pm.payment_tid, pm.user_master_key, pm.payment_state, pm.payment_date, pm.payment_approval_date, FN_GET_COMMON_CODE_NAME('PAYMENT_TYPE', pm.payment_type) payment_type_text, pm.payment_type, pm.payment_user_id, pm.payment_name, pm.payment_company_number, pm.product_master_key, pm.payment_product_name, pm.payment_product_price, pm.payment_use_time, pm.payment_ticket_amount, pm.payment_total_price, pm.payment_cancel_reason, pm.payment_cancel_person, pm.payment_cancel_date, pd.* FROM PAYMENT_MASTER pm, PAYMENT pd WHERE pm.payment_master_key = pd.payment_master_key AND
pm.payment_master_key = #{paymentMasterKey}
pm.payment_oid = #{oid} AND pm.payment_tid = #{tid}

mapper.update:
UPDATE PAYMENT
SET	 pay_state_cd	 = #{payStateCd}
	,pay_dt			 = #{payDt}
	,payer			 = #{payer}
	,cas_tamount	 = #{casTamount}
	,cas_camount	 = #{casCamount}
	,cas_flag		 = #{casFlag}
	,cas_seq_no		 = #{casSeqNo}
	,cash_receipt_self_yn = #{cashReceiptSelfYn}
	,cash_receipt_num = #{cashReceiptNum}
	,cash_receipt_kind = #{cashReceiptKind}
WHERE oid = #{oid} AND tid = #{tid}

UPDATE PAYMENT_MASTER
SET	 payment_state	 = #{payStateCd}
	,payment_date			 = #{payDt}
	,payment_name			 = #{payer}
WHERE payment_oid = #{oid} AND payment_tid = #{tid}

mapper.select:
SELECT COUNT(a.payment_state) paymentCount, COUNT(IF(a.payment_state = 1, 1, null)) paymentFinishCount, COUNT(IF(a.payment_state = 2, 1, null)) paymentWaitCount FROM ( SELECT payment_date, payment_state, FN_GET_COMMON_CODE_NAME('PAYMENT_STATE',payment_state) paymentStateText, payment_product_name, payment_type, FN_GET_COMMON_CODE_NAME('PAYMENT_TYPE', payment_type) paymentTypeText, payment_tid, payment_total_price FROM PAYMENT_MASTER WHERE user_master_key = #{userMasterKey}
UNION ALL
SELECT cm.contract_insert_date, cm.contract_state, FN_GET_COMMON_CODE_NAME('CONTRACT_STATE',contract_state) paymentStateText, cm.contract_product_name, 0, cm.contract_payment_method, 0, cm.contract_total_price FROM CONTRACT_MASTER cm, CONTRACT_USER cu WHERE cm.contract_master_key = cu.contract_master_key AND cu.user_master_key = #{userMasterKey} ) a WHERE 1 = 1
AND ( a.payment_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND a.payment_product_name = #{searchValue}
AND a.payment_type = #{searchValue1}
AND a.payment_state = #{searchValue2}

SELECT a.* FROM ( SELECT payment_date, payment_state, FN_GET_COMMON_CODE_NAME('PAYMENT_STATE',payment_state) paymentStateText, payment_product_name, payment_type, FN_GET_COMMON_CODE_NAME('PAYMENT_TYPE', payment_type) paymentTypeText, payment_tid, payment_total_price FROM PAYMENT_MASTER WHERE user_master_key = #{userMasterKey}
UNION ALL
SELECT cm.contract_insert_date, cm.contract_state, FN_GET_COMMON_CODE_NAME('CONTRACT_STATE',contract_state) paymentStateText, cm.contract_product_name, 0, cm.contract_payment_method, 0, cm.contract_total_price FROM CONTRACT_MASTER cm, CONTRACT_USER cu WHERE cm.contract_master_key = cu.contract_master_key AND cu.user_master_key = #{userMasterKey} ) a WHERE 1 = 1
AND ( a.payment_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND a.payment_product_name = #{searchValue}
AND a.payment_type = #{searchValue1}
AND a.payment_state = #{searchValue2}
LIMIT #{rowStartNumber}, #{pageListSize}
