mapper.select:
SELECT um.USER_MASTER_KEY, um.USER_ID, FN_GET_COMMON_CODE_NAME('USER_TYPE', um.USER_TYPE) USER_TYPE, um.USER_NAME, ud.USER_COMPANY_NAME, vr.*, vd.*, vd.manufacturer DEVICE_MAKER_CODE FROM VIEW_RESERVATIONS vr , VIEW_DEVICES vd, USER_MASTER um, USER_DETAIL ud WHERE vr.user_id = um.user_master_key AND um.user_master_key = ud.user_master_key AND vr.device_id = vd.id
AND ( vr.start_dttm BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') OR vr.end_dttm BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND um.USER_TYPE = #{searchValue}
AND vd.manufacturer= #{searchValue1}
AND um.USER_ID LIKE CONCAT(#{searchValue2}, '%')
AND (um.USER_NAME LIKE CONCAT(#{searchValue3}, '%') OR ud.USER_COMPANY_NAME LIKE CONCAT(#{searchValue3}, '%'))
ORDER BY start_dttm DESC LIMIT #{rowStartNumber}, #{pageListSize}

SELECT COUNT(*) FROM VIEW_RESERVATIONS vr , VIEW_DEVICES vd, USER_MASTER um, USER_DETAIL ud WHERE vr.user_id = um.user_master_key AND um.user_master_key = ud.user_master_key AND vr.device_id = vd.id
AND ( vr.start_dttm BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') OR vr.end_dttm BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND um.USER_TYPE = #{searchValue}
AND vd.manufacturer= #{searchValue1}
AND um.USER_ID LIKE CONCAT(#{searchValue2}, '%')
AND (um.USER_NAME LIKE CONCAT(#{searchValue3}, '%') OR ud.USER_COMPANY_NAME LIKE CONCAT(#{searchValue3}, '%'))

SELECT a.* FROM ( SELECT TPAS.ticket_product_service_key, TPAS.SERVICE_APPLY_DATE, TPAS.SERVICE_PRODUCT_NAME, TPAS.SERVICE_APPLY_TICKET_AMOUNT, TPAS.SERVICE_APPLY_TICKET_TOTAL_AMOUNT, FN_GET_COMMON_CODE_NAME( 'SERVICE_APPLY_REASON', TPAS.SERVICE_APPLY_REASON )SERVICE_APPLY_REASON, TPAS.SERVICE_APPLY_REASON_DETAIL, TPAS.SERVICE_APPLY_OPERATOR_NAME, FN_GET_COMMON_CODE_NAME( 'SERVICE_TARGET_TYPE', TPAS.SERVICE_TARGET_TYPE )SERVICE_TARGET_TYPE, TPAS.SERVICE_TARGET_NAME, TPAS.SERVICE_INSERT_DATE, CASE(SERVICE_TARGET_TYPE) WHEN 1 THEN 'member_all' WHEN 2 THEN 'person_all' WHEN 3 THEN 'company_all' WHEN 4 THEN ( SELECT um.USER_ID FROM USER_MASTER um WHERE um.USER_MASTER_KEY = ASU.USER_MASTER_KEY ) ELSE SERVICE_TARGET_NAME END AS USER_ID FROM TICKET_PRODUCT_APPLY_SERVICE TPAS, APPLY_SERVICE_USER ASU WHERE ASU.TICKET_PRODUCT_SERVICE_KEY = TPAS.TICKET_PRODUCT_SERVICE_KEY
AND TPAS.SERVICE_APPLY_DATE BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d')
AND SERVICE_TARGET_TYPE = #{searchValue}
AND SERVICE_APPLY_REASON = #{searchValue1}
AND TPAS.SERVICE_TARGET_NAME = #{searchValue3}
)a WHERE SERVICE_INSERT_DATE IS NOT NULL
AND USER_ID = #{searchValue2}
GROUP BY TICKET_PRODUCT_SERVICE_KEY, USER_ID ORDER BY SERVICE_INSERT_DATE DESC LIMIT #{rowStartNumber}, #{pageListSize}

SELECT count(*) FROM ( SELECT TPAS.ticket_product_service_key, TPAS.SERVICE_APPLY_DATE, TPAS.SERVICE_PRODUCT_NAME, TPAS.SERVICE_APPLY_TICKET_AMOUNT, TPAS.SERVICE_APPLY_TICKET_TOTAL_AMOUNT, FN_GET_COMMON_CODE_NAME( 'SERVICE_APPLY_REASON', TPAS.SERVICE_APPLY_REASON )SERVICE_APPLY_REASON, SERVICE_APPLY_REASON_DETAIL, TPAS.SERVICE_APPLY_OPERATOR_NAME, FN_GET_COMMON_CODE_NAME( 'SERVICE_TARGET_TYPE', TPAS.SERVICE_TARGET_TYPE )SERVICE_TARGET_TYPE, TPAS.SERVICE_TARGET_NAME, TPAS.SERVICE_INSERT_DATE, CASE(SERVICE_TARGET_TYPE) WHEN 1 THEN 'member_all' WHEN 2 THEN 'person_all' WHEN 3 THEN 'company_all' WHEN 4 THEN ( SELECT um.USER_ID FROM USER_MASTER um WHERE um.USER_MASTER_KEY = ASU.USER_MASTER_KEY ) ELSE SERVICE_TARGET_NAME END AS USER_ID FROM TICKET_PRODUCT_APPLY_SERVICE TPAS, APPLY_SERVICE_USER ASU WHERE ASU.TICKET_PRODUCT_SERVICE_KEY = TPAS.TICKET_PRODUCT_SERVICE_KEY
AND TPAS.SERVICE_APPLY_DATE BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d')
AND SERVICE_TARGET_TYPE = #{searchValue}
AND SERVICE_APPLY_REASON = #{searchValue1}
AND TPAS.SERVICE_TARGET_NAME = #{searchValue3}
GROUP BY ASU.TICKET_PRODUCT_SERVICE_KEY, user_id )a where SERVICE_INSERT_DATE is not null
AND USER_ID = #{searchValue2}

mapper.update:
UPDATE USER_TICKET_MASTER SET
ticket_avilable_amount = ticket_avilable_amount + ${serviceApplyTicketAmount}
,ticket_end_expiration_date = #{ticketEndExpirationDate}
,product_name = #{serviceProductName}
,product_master_key = #{productMasterKey}
WHERE user_master_key in ( SELECT user_master_key FROM USER_MASTER WHERE
USER_TYPE = '1' OR '2'
USER_TYPE = '1'
USER_TYPE = '2'
user_id = #{userId}
) AND use_yn = 'Y'

UPDATE
  USER_TICKET_MASTER
SET
  use_yn = #{useYn}
  ,modify_date = NOW()
WHERE
  user_master_key = #{userMasterKey}

  ORDER BY user_ticket_master_key DESC limit 1



    and user_ticket_master_key =  #{userTicketMasterKey}

UPDATE
  USER_TICKET_MASTER
SET
  ticket_avilable_amount = #{ticketAvilableAmount}
WHERE
  user_master_key = #{userMasterKey}
AND
  use_yn = 'Y'
AND
 user_ticket_master_key = #{userTicketMasterKey}

mapper.select:
SELECT COUNT(*) historyCount, COUNT(IF((`vr`.`status` IN (1)), 1, NULL)) useCount, COUNT(IF((`vr`.`status` in (0,6) AND vr.end_dttm > NOW()), 1, null)) reservationWaitCount, COUNT(IF((`vr`.`status` In(2,3) AND vr.end_dttm < NOW()), 1, null)) reservationFinishCount FROM VIEW_RESERVATIONS vr, VIEW_DEVICES vd WHERE vr.device_id = vd.id AND user_id = #{userMasterKey}
AND ( vr.start_dttm BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND vd.manufacturer = #{searchValue}
AND vd.prod_name = #{searchValue1}
AND
vr.status in (0,6) AND vr.end_dttm > NOW()
vr.end_dttm < NOW()
AND vr.product_name = #{searchValue3}

SELECT vr.id, vr.user_id, vr.device_id, vr.reservation_no, vr.reserve_time, vr.duration, vr.start_dttm, vr.end_dttm, vr.canceled, vr.reg_dttm, vr.updt_dttm, vr.use_point, vr.status,
( CASE WHEN (vr.status = '0') THEN '예약중' WHEN (vr.status = '1') THEN '사용중' WHEN ((vr.status = '2') AND (NOW() <= vr.end_dttm)) THEN '대기중' WHEN ((vr.status = '2') AND (NOW() > vr.end_dttm)) THEN '사용종료' WHEN (vr.status = '3') THEN '사용종료' ELSE '' END ) AS statusText, vd.manufacturer, vd.prod_name, vd.os, vd.resolution, vd.os_version, vr.product_name FROM VIEW_RESERVATIONS vr, VIEW_DEVICES vd WHERE vr.device_id = vd.id AND user_id = #{userMasterKey}
AND ( vr.start_dttm BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND vd.manufacturer = #{searchValue}
AND vd.prod_name = #{searchValue1}
AND
vr.status in (0,6) AND vr.end_dttm > NOW()
vr.end_dttm < NOW()
AND vr.product_name = #{searchValue3}
ORDER BY vr.start_dttm desc LIMIT #{rowStartNumber}, #{pageListSize}

SELECT COUNT(a.ticketApplyDate) historyCount FROM (
SELECT ticket_apply_date ticketApplyDate, '적립' statusText, ticket_amount historyUsedTicketAmountText,
ticket_start_expiration_date ticketStartExpirationDate, ticket_end_expiration_date ticketEndExpirationDate, product_name productName, '상품구매' service_apply_reason, '' service_apply_reason_detail , user_master_key FROM USER_TICKET_MASTER WHERE user_master_key = #{userMasterKey}
AND ( ticket_start_expiration_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') OR ticket_end_expiration_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND
'적립' = #{searchValue}
AND product_name = #{searchValue2}
AND user_ticket_master_key = #{userTicketMasterKey}
UNION ALL
SELECT service_apply_date ticketApplyDate,
IF ( service_apply_ticket_amount > 0, if(service_apply_reason > 6, '반환','적립'), '차감' ) AS statusText, service_apply_ticket_amount historyUsedTicketAmountText, tr.ticket_start_expiration_date ticketStartExpirationDate, tr.ticket_end_expiration_date ticketEndExpirationDate, tr.service_product_name productName, FN_GET_COMMON_CODE_NAME ( 'SERVICE_APPLY_REASON', service_apply_reason ) service_apply_reason, service_apply_reason_detail , au.user_master_key FROM TICKET_PRODUCT_APPLY_SERVICE tr INNER JOIN APPLY_SERVICE_USER au on tr.ticket_product_service_key = au.ticket_product_service_key LEFT OUTER JOIN USER_TICKET_MASTER ut on tr.user_ticket_master_key = ut.user_ticket_master_key WHERE au.user_master_key = #{userMasterKey}
AND ( tr.ticket_start_expiration_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') OR tr.ticket_end_expiration_date BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND IF ( service_apply_ticket_amount > 0, if(service_apply_reason > 6, '반환','적립'), '차감' ) = #{searchValue}
AND tr.service_product_name = #{searchValue2}
AND ut.user_ticket_master_key = #{userTicketMasterKey}
UNION ALL
SELECT reserve_time, '차감' statusText, use_point, start_dttm, end_dttm, vr.product_name, '-' service_apply_reason, '' service_apply_reason_detail, vr.user_id FROM VIEW_RESERVATIONS vr, VIEW_DEVICES vd WHERE vr.device_id = vd.id AND vr.user_id = #{userMasterKey}
AND ( start_dttm BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') OR end_dttm BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND '차감' = #{searchValue}
AND product_name = #{searchValue2}
AND vr.user_ticket_master_key = #{userTicketMasterKey}
) a WHERE a.user_master_key = #{userMasterKey}

SELECT a.* FROM (
SELECT ticket_apply_date ticketApplyDate, '적립' statusText, -- ticket_amount historyUsedTicketAmountText, if(product_master_key = 1,ticket_amount,(ticket_amount*5)) historyUsedTicketAmountText, ticket_avilable_amount ticketAvilableAmount, ticket_start_expiration_date ticketStartExpirationDate, ticket_end_expiration_date ticketEndExpirationDate, product_name productName, if(product_master_key = 1, '가입이벤트','상품구매') service_apply_reason, '' service_apply_reason_detail , user_master_key, user_ticket_master_key FROM USER_TICKET_MASTER WHERE user_master_key = #{userMasterKey}
UNION ALL
SELECT service_apply_date ticketApplyDate, IF ( service_apply_ticket_amount > 0, if(service_apply_reason > 6, '반환','적립'), '차감' ) AS statusText, service_apply_ticket_amount historyUsedTicketAmountText, ut.ticket_avilable_amount ticketAvilableAmount, tr.ticket_start_expiration_date ticketStartExpirationDate, tr.ticket_end_expiration_date ticketEndExpirationDate, tr.service_product_name productName, FN_GET_COMMON_CODE_NAME ( 'SERVICE_APPLY_REASON', service_apply_reason ) service_apply_reason, service_apply_reason_detail , au.user_master_key, ut.user_ticket_master_key FROM TICKET_PRODUCT_APPLY_SERVICE tr INNER JOIN APPLY_SERVICE_USER au on tr.ticket_product_service_key = au.ticket_product_service_key LEFT OUTER JOIN USER_TICKET_MASTER ut on tr.user_ticket_master_key = ut.user_ticket_master_key WHERE au.user_master_key = #{userMasterKey}
UNION ALL
SELECT vr.reserve_time, '차감' , use_point, (select utm.ticket_avilable_amount from user_ticket_master utm where utm.user_ticket_master_key=vr.user_ticket_master_key) ticketAvilableAmount, -- '' ticketAvilableAmount, vr.start_dttm, vr.end_dttm, vr.product_name, if(now() > vr.start_dttm, '시간 사용', '디바이스 예약') service_apply_reason,
CONCAT(vd.prod_name,'/',concat(vr.start_dttm,'~',vr.end_dttm)) service_apply_reason_detail, vr.user_id, vr.user_ticket_master_key FROM VIEW_RESERVATIONS vr ,VIEW_DEVICES vd
WHERE vr.device_id = vd.id
AND vr.user_id = #{userMasterKey} ) a WHERE a.user_master_key = #{userMasterKey}
AND ( a.ticketStartExpirationDate BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') OR a.ticketEndExpirationDate BETWEEN DATE_FORMAT(#{startDate}, '%Y-%m-%d') AND DATE_FORMAT(ADDDATE(#{endDate},+1), '%Y-%m-%d') )
AND a.statusText = #{searchValue}
AND a.productName = #{searchValue2}
AND a.user_ticket_master_key = #{userTicketMasterKey}
ORDER BY ticketApplyDate DESC LIMIT #{rowStartNumber}, #{pageListSize}

SELECT uth.user_ticket_master_key, uth.user_master_key, uth.product_master_key, uth.ticket_amount, uth.product_name, uth.ticket_avilable_amount, uth.ticket_start_expiration_date, uth.ticket_end_expiration_date, uth.ticket_apply_date, uth.insert_date, if(use_yn = 'N' , 0, TO_DAYS(uth.ticket_end_expiration_date) - TO_DAYS(now())) as expirationDate, (ifnull((select sum(vr.use_point) FROM VIEW_RESERVATIONS AS vr WHERE vr.user_id = uth.user_master_key AND vr.user_ticket_master_key = uth.user_ticket_master_key), 0)) as usePoint,
if(uth.ticket_avilable_amount > 0, if(use_yn = 'Y' and TO_DAYS(uth.ticket_end_expiration_date) - TO_DAYS(now()) > 0, if(TO_DAYS(uth.ticket_start_expiration_date) - TO_DAYS(now()) > 0, '사용대기', '사용중'), '사용종료'), '사용종료') useYn FROM USER_TICKET_MASTER uth WHERE user_master_key = #{userMasterKey}
and product_master_key =#{productMasterKey}
ORDER BY uth.insert_date desc

